pragma solidity ^0.8.9;

import "@chainlink/contracts/src/v0.8/ConfirmedOwner.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/interfaces/KeeperCompatibleInterface.sol";

error Raffle_NotEnoughETHEntered();
error Raffle_TransferFail();
error Raffle_NotOpen();
error Raffle_UpkeepNotNeeded(uint256 currentBalance , uint256 numPlayers , uint256 rafflestate);

contract Raffle is VRFConsumerBaseV2,KeeperCompatibleInterface{
    /*Type*/
    enum RaffleState {
        OPEN,
        CALCULATING
    }

    /*State Variables */
    uint256 private immutable i_entranceFee;
    address payable[] private s_player;
    VRFCoordinatorV2Interface private immutable i_vrfCoordinator;
    bytes32 private immutable i_gasLane;
    uint64 private immutable i_subscriptionId;
    uint32 private immutable i_callbackGasLimit;
    uint16 private constant REQUEST_COFIRMATIONS=3;
    uint32 private constant NUM_WORDS=1;

    //Lottery variables
    address private s_recentWinner;
    RaffleState private s_raffleState;
    uint256 private s_lastTimeStamp;
    uint256 private immutable i_Interval;

    /*Events*/
    event RaffleEnter(address indexed s_player);
    event RequestedRaffleWinner(uint256 indexed requestId);
    event WinnerPicked(address indexed winner);

    constructor(
        address vrfCoordinatorV2, //contract address
        uint256 entranceFee,
        bytes32 gasLane,
        uint64 subscriptionId,
        uint32 callbackGasLimit,
        uint256 interval
        ) VRFConsumerBaseV2(vrfCoordinatorV2){
        i_entranceFee=entranceFee;
        i_vrfCoordinator=VRFCoordinatorV2Interface(vrfCoordinatorV2);
        i_gasLane=gasLane;
        i_subscriptionId=subscriptionId;
        i_callbackGasLimit=callbackGasLimit;
        s_raffleState=RaffleState.OPEN;
        s_lastTimeStamp=block.timestamp;
        i_Interval=interval;
    }

    function enterRaffle()public payable{
        if(msg.value<i_entranceFee){revert Raffle_NotEnoughETHEntered();}
        if(s_raffleState!=RaffleState.OPEN){revert Raffle_NotOpen();}
        s_player.push(payable(msg.sender));
        emit RaffleEnter(msg.sender);
    }

    /* This is the function that Chainlin  Keeper nodes 
     *call they look for the 'upKeepNeeded to return true.
     *The following should be true in order to return true:
     *1.Our time interval should have passed
     *2.The lottery should have at least 1 player, and have some ETH
     *3.Our subscribtion is funded with Link
     *4.The lottery is in 'open' state
     */

    function checkUpkeep( bytes memory /*checkData*/)public view override 
    returns(bool upkeepNeeded,bytes memory){
            bool isOpen=(RaffleState.OPEN==s_raffleState);
            bool timePassed=((block.timestamp-s_lastTimeStamp)>i_Interval);
            bool hasPlayers = (s_player.length>0);
            bool hasBalance = (address(this).balance>0);
            upkeepNeeded = (isOpen && timePassed && hasBalance && hasPlayers);
    }


    function performUpkeep(bytes calldata)external override{
        (bool upkeepNeeded,) = checkUpkeep("");
        if(!upkeepNeeded){
            revert Raffle_UpkeepNotNeeded(
                address(this).balance,
                s_player.length,
                uint256(s_raffleState)
            );
        }
        //request the random number
        //Once get it ,do someting 
        s_raffleState=RaffleState.CALCULATING;
        uint256 requestId=i_vrfCoordinator.requestRandomWords(
            i_gasLane,
            i_subscriptionId,
            REQUEST_COFIRMATIONS,
            i_callbackGasLimit,
            NUM_WORDS
        );
        emit RequestedRaffleWinner(requestId);

    }

    function fulfillRandomWords(uint256 /*requestId*/,uint256 [] memory randomwords) internal override{
        uint256 indexOfWinner = randomwords[0] % s_player.length;
        address payable recentWinner = s_player[indexOfWinner];
        s_recentWinner=recentWinner;
        s_raffleState=RaffleState.OPEN;
        s_player = new address payable[](0);
        s_lastTimeStamp=block.timestamp;
        (bool success,)=recentWinner.call{value: address(this).balance}("");
        if(!success){
            revert Raffle_TransferFail();
        }
        emit WinnerPicked(recentWinner);
    }

    /* View | pure */
    function getEntranceFee()public view returns(uint256){
        return i_entranceFee;
    }

    function getPlayer(uint256 index) public view returns(address){
        return s_player[index];
    }
    function getReceneWinner() public view returns(address){
        return s_recentWinner;
    }
    function getInterval() public view returns(uint256){
        return i_Interval;
    }

    function getRaffleState() public view returns(RaffleState){
        return s_raffleState;
    }
    function getlastTimeStamp() public view returns(uint256){
        return s_lastTimeStamp;
    }
    function getNumberOfPlayers() public view returns(uint256){
        return s_player.length;
    }
    // function pickwinner(){

    // }
}","keccak256":"0xc8b4af68cd871320e18abd8a034ad42dfef5c21dbb9b7ee3bc246d2ade3c56b2","license":"MIT"}},"version":1}
